import React from 'react';
import { useSelector } from 'react-redux';
import {Card, Button} from 'react-bootstrap';

const Profile = () => {
    const user = useSelector((state)=> state.userReducer.user)
    return <div>
        <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src="https://www.google.com/url?sa=i&url=https%3A%2F%2Fwww.freepik.com%2Ffree-photos-vectors%2Funknown-profile&psig=AOvVaw0j1nFFMgg-zmcJg4mbF9z0&ust=1708024688334000&source=images&cd=vfe&opi=89978449&ved=0CBMQjRxqFwoTCNiNif7Fq4QDFQAAAAAdAAAAABAS" alt='profile'/>
      <Card.Body>
        <Card.Title>Welcome {user && user.firstname}</Card.Title>
        <Button variant="danger" href='/tasks'>My Tasks</Button>
      </Card.Body>
    </Card>
    </div>
  
}

export default Profile
