import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTask, getTasks } from "../JS/Actions/task";
import Task from '../Components/Task/Task';

const TaskPage = () => {
  const allTasks = useSelector((state) => state.taskReducer.allTasks);
  const load = useSelector((state) => state.taskReducer.load);
  const dispatch = useDispatch();
  const match = useMatch("/edit/:id");

  useEffect(() => {
    dispatch(getTask(match.params.id));
  })
  
  useEffect(() => {
    dispatch(getTasks());
  }, [dispatch]);

  return (
    <div>
          {load ? <h2>Loading...</h2> : allTasks.map((el)=> <Task task={el} key={el.id}/>)}
          
    </div>
  );
};

export default TaskPage;
