import React from 'react';
import { useSelector } from 'react-redux';
import {Card, Button} from 'react-bootstrap';

const Profile = () => {
    const user = useSelector((state)=> state.userReducer.user)
    return <div>
        <Card style={{ width: '18rem' }}>
      <Card.Img variant="top" src="https://img.freepik.com/premium-vector/man-avatar-profile-picture-vector-illustration_268834-538.jpg?size=338&ext=jpg&ga=GA1.1.87170709.1707782400&semt=ais" alt='profile' width="10" height="180"/>
      <Card.Body>
        <Card.Title>Welcome {user && user.firstname}</Card.Title>
        <Button variant="danger" href='/tasks'>My Tasks</Button>
      </Card.Body>
    </Card>
    </div>
  
}

export default Profile
