import React, { useEffect, useState } from 'react';
import { Button, Modal, ModalBody } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { editTask, getTask } from '../../JS/Actions/task';
import { useMatch } from 'react-router-dom';

const Edit = ({task}) => {
    const [show, setShow] = useState(false);
    const [newTask, setNewTask] = useState({task: ''});
    const dispatch = useDispatch()
    const oneTask = useSelector((state) => state.taskReducer.oneTask);
    const match = useMatch("/edit/:id");
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);


    const handleChange = (e) => {
        setNewTask({ ...newTask, [e.target.name]: e.target.value });
      };

      useEffect(() => {
        dispatch(getTask(task._id));
      });
      const handleEdit = () => {
        dispatch(editTask(task._, newTask));
        ;
      };

  return (
    <>
    <Button variant='info' onClick={handleShow}>
        EDIT
    </Button>
    <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
            <Modal.Title>Change the Task</Modal.Title>
        </Modal.Header>
        <ModalBody>
            <input value={newTask.task} 
            onChange={(e)=> setNewTask(e.target.value)}/>
        </ModalBody>
        <Modal.Footer>
            <Button variant='secondary' onClick={handleClose}>Close</Button>
            <Button variant='primary'  onClick={handleEdit}
>Save Changes</Button>
        </Modal.Footer>
    </Modal>
    </>

  );
}

export default Edit
