import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getTask, getTasks } from "../JS/Actions/task";
import Task from '../Components/Task/Task';
import { useMatch, useParams } from "react-router-dom";

const TaskPage = ({task}) => {
  const allTasks = useSelector((state) => state.taskReducer.allTasks);
  const load = useSelector((state) => state.taskReducer.load);
  const dispatch = useDispatch();
  const {id} = useParams()


  useEffect(() => {
    dispatch(getTask(task.id));
  }, [dispatch]);
  
  useEffect(() => {
    dispatch(getTasks());
  }, [dispatch]);

  return (
    <div>
          {load ? <h2>Loading...</h2> : allTasks.map((el)=> <Task task={el} key={el.id}/>)}
          
    </div>
  );
};

export default TaskPage;
